SELECT rc.Dep_Airport_ID, r.Runway_ID, rc.Num_of_Runways, r.`Length`, r.Width, r.Surface_Type_Condition, r.Edge_Light_Intensity, r.WBC_Single, r.WBC_Dual, r.WBC_Two_Dual, r.WBC_Tandem_Two_Dual, r.Base_Marking_Condition
FROM runway_counts as rc
LEFT JOIN `runways`.`runway` as r ON rc.Dep_Airport_ID = r.Dep_Airport_ID;

-- Runway table query, version 3
SELECT sch.Dep_Airport_ID, sch.Schedules, sch.ARP_Latitude, sch.ARP_Longitude, sch.Facility_Type, sch.Wind_Indicator, run.Runway_ID, run.Num_of_Runways, run.Length, run.Width, run.Surface_Type_Condition, run.Edge_Light_Intensity, run.WBC_Single, run.WBC_Dual, run.WBC_Two_Dual, run.WBC_Tandem_Two_Dual, run.Base_Marking_Condition
FROM `airport_schedules_on_airports (without when clause)` AS sch
INNER JOIN   (SELECT rc.Dep_Airport_ID, r.Runway_ID, rc.Num_of_Runways, r.`Length`, r.Width, r.Surface_Type_Condition, r.Edge_Light_Intensity, r.WBC_Single, r.WBC_Dual, r.WBC_Two_Dual, r.WBC_Tandem_Two_Dual, r.Base_Marking_Condition
FROM runway_counts as rc
LEFT JOIN `runways`.`runway` as r ON rc.Dep_Airport_ID = r.Dep_Airport_ID)   AS run 
ON sch.Dep_Airport_ID = run.Dep_Airport_ID
